mapper.select:
SELECT *
FROM discuss_post
WHERE status != 2

    AND user_id = #{userId}

ORDER BY type DESC, create_time DESC
LIMIT #{offset}, #{limit};

mapper.update:
UPDATE discuss_post
SET comment_count = #{commentCount}
WHERE id = #{id}

UPDATE discuss_post
SET type = #{type}
WHERE id = #{id}

UPDATE discuss_post
SET status = #{status}
WHERE id = #{id}
